package logproof

import (
	"math/big"
	"testing"

	"github.com/bnb-chain/tss-lib/v2/crypto"
	"github.com/bnb-chain/tss-lib/v2/crypto/paillier"

	"github.com/decred/dcrd/dcrec/edwards/v2"

	"github.com/stretchr/testify/assert"
)

var (
	config   = crypto.NewProofConfig(edwards.Edwards().N)
	p0, _    = new(big.Int).SetString("", 10)
	q0, _    = new(big.Int).SetString("", 10)
	n0       = new(big.Int).Mul(p0, q0)
	n0Square = new(big.Int).Exp(n0, big2, nil)
	ssIDInfo = []byte("Mark HaHa")
	pedp, _  = new(big.Int).SetString("", 10)
	pedq, _  = new(big.Int).SetString("", 10)
	pedN     = new(big.Int).Mul(pedp, pedq)
	pedT     = big.NewInt(9)
	pedS     = big.NewInt(729)
	ped      = &paillier.PedPubKey{
		N: pedN,
		S: pedS,
		T: pedT,
	}
)

func TestEncProof(test *testing.T) {
	x := big.NewInt(3)
	rho := big.NewInt(103)
	C := new(big.Int).Mul(new(big.Int).Exp(new(big.Int).Add(big1, n0), x, n0Square), new(big.Int).Exp(rho, n0, n0Square))
	C.Mod(C, n0Square)
	X := crypto.ScalarBaseMult(edwards.Edwards(), x)
	G, _ := crypto.NewECPoint(X.Curve(), X.Curve().Params().Gx, X.Curve().Params().Gy)

	// ok
	zkproof, err := NewKnowExponentAndPaillierEncryption(config, ssIDInfo, x, rho, C, n0, ped, X, G)
	assert.NoError(test, err)
	err = zkproof.Verify(config, ssIDInfo, C, n0, ped, X, G)
	assert.NoError(test, err)

	// not in range
	config.TwoExpLAddepsilon = big.NewInt(-1)
	zkproof, err = NewKnowExponentAndPaillierEncryption(config, ssIDInfo, x, rho, C, n0, ped, X, G)
	assert.Error(test, err)
	assert.Empty(test, zkproof)
}

func TestRealLogProof(test *testing.T) {
	ec := edwards.Edwards()
	proofParameter := crypto.NewProofConfig(ec.N)
	contextI := []byte{245, 179, 33, 71, 4, 237, 175, 194, 197, 194, 88, 106, 74, 171, 194, 239, 152, 246, 248, 35, 239, 229, 97, 145, 241, 137, 110, 236, 124, 25, 104, 62}
	kCiphertexts, _ := new(big.Int).SetString("", 10)
	paillierPKN, _ := new(big.Int).SetString("", 10)

	N, _ := new(big.Int).SetString("", 10)
	S, _ := new(big.Int).SetString("", 10)
	T, _ := new(big.Int).SetString("", 10)
	ringPedersenPK := &paillier.PedPubKey{
		N: N,
		S: S,
		T: T,
	}

	RiX, _ := new(big.Int).SetString("10572993586516734219551332451747528308801788412033618686576841475641596029568", 10)
	RiY, _ := new(big.Int).SetString("20876302078176891242215373638582567540160618843303179521783876273127701350698", 10)
	Ri, _ := crypto.NewECPoint(ec, RiX, RiY)
	G, _ := crypto.NewECPoint(ec, ec.Params().Gx, ec.Params().Gy)

	k, _ := new(big.Int).SetString("6243111752332290845546866078309593702173971284255407247776457075243936826889", 10)
	rho, _ := new(big.Int).SetString("", 10)

	zkproof, err := NewKnowExponentAndPaillierEncryption(proofParameter, contextI, k, rho, kCiphertexts, paillierPKN, ringPedersenPK, Ri, G)
	assert.NoError(test, err)

	err = zkproof.Verify(proofParameter, contextI, kCiphertexts, paillierPKN, ringPedersenPK, Ri, G)
	assert.NoError(test, err)
}
